package com.example.secondhandWeb.controller;


import com.example.secondhandWeb.po.Orders;
import com.example.secondhandWeb.po.User;
import com.example.secondhandWeb.service.ManagerService;
import com.example.secondhandWeb.service.UserService;
import io.swagger.models.auth.In;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.*;

import java.util.List;

/**
 * <p>
 *  前端控制器
 * </p>
 *
 * @author testjava
 * @since 2021-04-18
 */
@RestController
@RequestMapping("/secondhandWeb/user")
public class UserController {
    @Autowired
    private UserService userService;

//    用户登录
    @PostMapping("/userLogin")
    public User userLogin(String userName,String userPassword){
        return userService.userLogin(userName,userPassword);
    }

//    是否存在相同的用户名
    @PostMapping("isExistUserName")
    public int isExistUserName(String userName){
        return userService.isExistUserName(userName);
    }

//    是否存在相同的手机号
    @PostMapping("isExistUserPhone")
    public int isExistUserPhone(String userPhone){
        return userService.isExistUserPhone(userPhone);
    }

//    用户注册
    @PostMapping("userRegister")
    public int userRegister(String userName,String userPhone, String userEmail,String userSchool, Boolean userSex, String userPassword){
        User user=new User();
        user.setUserName(userName);
        user.setUserPhone(userPhone);
        user.setUserEmail(userEmail);
        user.setUserSchool(userSchool);
        user.setUserSex(userSex);
        user.setUserPassword(userPassword);
        user.setUserImage("data:image/png;base64,");

        return userService.userRegister(user);
    }

    //    获取用户列表
    @PostMapping("/getUserList")
    public List<User> getUserList(int pageNo,int pageSize){
        return userService.getUserList(pageNo,pageSize);
    }

//    根据用户名模糊查询
    @PostMapping("/findUserByName")
    public List<User> findUserByName(String userName){
        return userService.findUserByName(userName);
    }

    //根据学校模糊查询用户
    @PostMapping("findUserBySchool")
    public List<User> findUserBySchool(String userSchool){return userService.findUserBySchool(userSchool);}

//    获取用户总数
    @GetMapping("/getUserCount")
    public int getUserCount() {
        return userService.getUserCount();
    }

    //更新用户列表
    @PostMapping("updateUser")
    public User updateUser(Long userId){
        return userService.updateUser(userId);
    }

    //编辑用户信息
    @PostMapping("editUser")
    public int editUser(User user){
        return userService.editUser(user);
    }

    //获取某个用户信息
    @PostMapping("getUserInfo/{userId}")
    public User getUserInfo(@PathVariable Long userId){return userService.getUserInfo(userId);}
}


