mapper.select:
        SELECT `attention`.`attentId`,`attention`.`userId`,`attention`.`beAttentId`,`user`.`userImage`,`user`.`userName`,`user`.`userSchool`,`user`.`publishCount`,`user`.`attentCount`,`user`.`fansCount`
FROM `attention` INNER JOIN `user` ON `attention`.`beAttentId`=`user`.`userId`
WHERE `attention`.`userId`=#{userId}

