mapper.select:
        SELECT `goods`.`goodsFavorite`,`goods`.`goodsId`,`goods`.`goodsImage`,`goods`.`goodsClass`,`goods`.`goodsIntroduct`,`goods`.`goodsName`,`goods`.`goodsOldNew`,`goods`.`goodsPrice`,`goods`.`goodsCar`,`goods`.`isSell`,`goods`.`sellerId`,`user`.`userName`,`user`.`userPhone`,`user`.`userSex`,`user`.`userImage`,`user`.`userSchool`,`user`.`attentCount`,`user`.`fansCount`
FROM `goods` INNER JOIN `user` ON `goods`.`sellerId`=`user`.`userId`
WHERE `goods`.`goodsId`=#{goodsId}
