mapper.select:
    SELECT `symess`.`symessName`,`symess`.`symessT`,`goods`.`goodsName`,`goods`.`goodsImage`,`goods`.`goodsPrice`,`symess`.`isRead`,`symess`.`symessCont`,`symess`.`symessImp`,`symess`.`symessWhy`
FROM `symess` INNER JOIN `goods` ON `symess`.`goodsId`=`goods`.`goodsId`
WHERE `symess`.`symessId`=#{symessId}
